import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Livro } from '../model/livro';
import { Observable } from 'rxjs';


interface LivroMongo {
  _id: string | null;
  codEditora: number;
  titulo: string;
  resumo: string;
  autores: string[];
}

const baseURL = "http://localhost:3030/livros";

@Injectable({
  providedIn: 'root'
})
export class ControleLivrosService {

  constructor(private http: HttpClient) { }

  obterLivros(): Observable<Livro[]> {
    return this.http.get<Livro[]>(baseURL);
  }

  incluir(livro: Livro): Observable<Livro> {
    return this.http.post<Livro>(baseURL, livro);
  }

  excluir(codigo: string): Observable<void> {
    return this.http.delete<void>(`${baseURL}/${codigo}`);
  }
}
